pragma solidity ^0.4.2;
import "./PatientContract.sol";

contract Hospital {
    address admin;
    address admin2;
    struct Info {
        address patient;
        uint dateCheckIn;
        uint dateCheckOut;
    }
    mapping(address => Info[]) public patients;
    mapping(address => bool) public hospitals;
    address public patient;
    Patient public patientContract;

    constructor() public {
        admin = 0xcB000A6BD06Efed410B8438100C46C98f685c616;
        admin2 = 0x6320CE7E63B52fEb7498170f4316B4d46a5f7d82;
    }

    function startTreatment(address _patientContract,address _patient, uint date) public {
        require(hospitals[msg.sender] == true, "Only hospital in the system can do this");
        patientContract = Patient(_patientContract);
        patients[_patient].push(Info(patientContract,date, 0));
    }
    
    function addHospital(address _hospital) public {
        require(msg.sender == admin || msg.sender == admin2, "Only admin can do this");
        hospitals[_hospital] = true;
    }
    
    
}
