pragma solidity ^0.4.2;

contract Patient {    
    string public medicalHistory;
    uint public breath;
    uint public bloodPressureMin;
    uint public bloodPressureMax;
    uint public weight;        
    uint public pulse;         
    uint public temperature;   
    string department;
    string public description; 
    address public doctor;
    address public hospital;
    constructor (string _medicalHistory,
                uint _breath, uint _bloodPressureMin, 
                uint _bloodPressureMax, uint _weight, 
                uint _pulse, uint _temperature,
                string _description, address _doctor,
                address _hospital,
                string _department
    ) public {
        medicalHistory = _medicalHistory;
        breath = _breath;
        bloodPressureMin = _bloodPressureMin;
        bloodPressureMax = _bloodPressureMax;
        weight = _weight;
        pulse = _pulse;
        temperature = _temperature;     
        description = _description;     
        doctor = _doctor;
        hospital = _hospital;
        department = _department;
    }
        
}
