pragma solidity ^0.4.2;        
import "./PatientContract.sol";

contract Hospital {
    
    address admin;             
    address admin2;
    
    struct PatientContractInfo {
        address patientContractAddress;       
        uint dateCheckIn;      
        uint dateCheckOut;     
    }
    
    struct Info {
        string gender;
        uint dateOfBirth;
    }
    
    mapping(address => PatientContractInfo[]) public patients;
    mapping(address => Info) public patientInfo;
    mapping(address => bool) public hospitals;
    
    address public patient;
    Patient public patientContract; 
    
    constructor() public {
        admin = 0xcB000A6BD06Efed410B8438100C46C98f685c616;
        admin2 = 0x6320CE7E63B52fEb7498170f4316B4d46a5f7d82;
    }

    function getPatientsLength (address _patientAddress) public view returns (uint) {
        return patients[_patientAddress].length;
    }
    function startTreatment(address _patientContract, address _patient, uint _date) public {
        require(hospitals[msg.sender] == true, "Only hospital in the system can do this");
        patientContract = Patient(_patientContract);
        patients[_patient].push(PatientContractInfo(patientContract, _date, 0));
    }
    
    function endTreatment(address _patient, uint _date, address _patientContractAddress) {
        require(hospitals[msg.sender] == true, "Only hospital in the system can do this");
        for (uint i = 0; i< patients[_patient].length; i+=1) {
            if (patients[_patient][i].patientContractAddress == _patientContractAddress) {
                patients[_patient][i].dateCheckOut = _date;
                break;
            }
        }
    }
    
    function updatePatientInfo(address _patient ,string _gender, uint _dateOfBirth) {
        require(msg.sender == admin || msg.sender == admin2, "Only admin can do this");
        patientInfo[_patient] = Info(_gender, _dateOfBirth);
    }
    
    function addHospital(address _hospital) public {
        require(msg.sender == admin || msg.sender == admin2, "Only admin can do this");
        hospitals[_hospital] = true;    
    }
    
}
